  end

  describe "GET new" do
    it "assigns a new academic_calander as @academic_calander" do
      AcademicCalander.stub(:new) { mock_academic_calander }
      get :new
      assigns(:academic_calander).should be(mock_academic_calander)
    end
  end

  describe "GET edit" do
    it "assigns the requested academic_calander as @academic_calander" do
      AcademicCalander.stub(:find).with("37") { mock_academic_calander }
