      it "redirects to the created academic_calander" do
        AcademicCalander.stub(:new) { mock_academic_calander(:save => true) }
        post :create, :academic_calander => {}
        response.should redirect_to(academic_calander_url(mock_academic_calander))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved academic_calander as @academic_calander" do
        AcademicCalander.stub(:new).with({'these' => 'params'}) { mock_academic_calander(:save => false) }
        post :create, :academic_calander => {'these' => 'params'}
        assigns(:academic_calander).should be(mock_academic_calander)
