      end

      it "assigns the requested academic_calander as @academic_calander" do
        AcademicCalander.stub(:find) { mock_academic_calander(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:academic_calander).should be(mock_academic_calander)
      end

      it "redirects to the academic_calander" do
        AcademicCalander.stub(:find) { mock_academic_calander(:update_attributes => true) }
