    @mock_course_assignment ||= mock_model(CourseAssignment, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all course_assignments as @course_assignments" do
      CourseAssignment.stub(:all) { [mock_course_assignment] }
      get :index
      assigns(:course_assignments).should eq([mock_course_assignment])
    end
  end

  describe "GET show" do
    it "assigns the requested course_assignment as @course_assignment" do
      CourseAssignment.stub(:find).with("37") { mock_course_assignment }
      get :show, :id => "37"
      assigns(:course_assignment).should be(mock_course_assignment)
    end
  end

  describe "GET new" do
    it "assigns a new course_assignment as @course_assignment" do
      CourseAssignment.stub(:new) { mock_course_assignment }
      get :new
      assigns(:course_assignment).should be(mock_course_assignment)
    end
  end

  describe "GET edit" do
    it "assigns the requested course_assignment as @course_assignment" do
      CourseAssignment.stub(:find).with("37") { mock_course_assignment }
      get :edit, :id => "37"
      assigns(:course_assignment).should be(mock_course_assignment)
    end
  end

  describe "POST create" do
