      it "assigns a newly created course_assignment as @course_assignment" do
        CourseAssignment.stub(:new).with({'these' => 'params'}) { mock_course_assignment(:save => true) }
        post :create, :course_assignment => {'these' => 'params'}
        assigns(:course_assignment).should be(mock_course_assignment)
      end

      it "redirects to the created course_assignment" do
        CourseAssignment.stub(:new) { mock_course_assignment(:save => true) }
        post :create, :course_assignment => {}
        response.should redirect_to(course_assignment_url(mock_course_assignment))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved course_assignment as @course_assignment" do
        CourseAssignment.stub(:new).with({'these' => 'params'}) { mock_course_assignment(:save => false) }
        post :create, :course_assignment => {'these' => 'params'}
        assigns(:course_assignment).should be(mock_course_assignment)
      end

      it "re-renders the 'new' template" do
        CourseAssignment.stub(:new) { mock_course_assignment(:save => false) }
        post :create, :course_assignment => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested course_assignment" do
        CourseAssignment.stub(:find).with("37") { mock_course_assignment }
        mock_course_assignment.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :course_assignment => {'these' => 'params'}
