      it "assigns the requested course_assignment as @course_assignment" do
        CourseAssignment.stub(:find) { mock_course_assignment(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:course_assignment).should be(mock_course_assignment)
      end

      it "redirects to the course_assignment" do
        CourseAssignment.stub(:find) { mock_course_assignment(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(course_assignment_url(mock_course_assignment))
      end
    end

    describe "with invalid params" do
      it "assigns the course_assignment as @course_assignment" do
        CourseAssignment.stub(:find) { mock_course_assignment(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:course_assignment).should be(mock_course_assignment)
