      assigns(:relevant_publication).should be(mock_relevant_publication)
    end
  end

  describe "GET edit" do
    it "assigns the requested relevant_publication as @relevant_publication" do
      RelevantPublication.stub(:find).with("37") { mock_relevant_publication }
      get :edit, :id => "37"
      assigns(:relevant_publication).should be(mock_relevant_publication)
