      it "assigns a newly created but unsaved relevant_publication as @relevant_publication" do
        RelevantPublication.stub(:new).with({'these' => 'params'}) { mock_relevant_publication(:save => false) }
        post :create, :relevant_publication => {'these' => 'params'}
        assigns(:relevant_publication).should be(mock_relevant_publication)
      end

      it "re-renders the 'new' template" do
        RelevantPublication.stub(:new) { mock_relevant_publication(:save => false) }
        post :create, :relevant_publication => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
