        put :update, :id => "1"
        response.should redirect_to(relevant_publication_url(mock_relevant_publication))
      end
    end

    describe "with invalid params" do
      it "assigns the relevant_publication as @relevant_publication" do
        RelevantPublication.stub(:find) { mock_relevant_publication(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:relevant_publication).should be(mock_relevant_publication)
