      it "redirects to the service_agreement" do
        ServiceAgreement.stub(:find) { mock_service_agreement(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(service_agreement_url(mock_service_agreement))
      end
    end

    describe "with invalid params" do
      it "assigns the service_agreement as @service_agreement" do
        ServiceAgreement.stub(:find) { mock_service_agreement(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:service_agreement).should be(mock_service_agreement)
      end

      it "re-renders the 'edit' template" do
        ServiceAgreement.stub(:find) { mock_service_agreement(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
